const pesquisa = document.getElementById('google-input-search')

document.getElementById("google-input-search").onkeypress = function (e) {
  // 13 Ã© a tecla <ENTER>. Se ela for pressionada, mostrar o valor
  if (e.keyCode === 13) {
    if (pesquisa !== null || pesquisa.value > 0) {
      const pesquisaGoogle = pesquisa.value.replace(/\s/g, '+')

      window.location.href = `https://www.google.com/search?ei=XRzJX7fKOa-_5OUP1rSUkAQ&q=${pesquisaGoogle}`
    }

    return false;
  }
}

function pesquisar() {
  if (pesquisa.value !== null || pesquisa.value > 0) {
    const pesquisaGoogle = pesquisa.value.replace(/\s/g, '+')

    window.location.href = `https://www.google.com/search?ei=XRzJX7fKOa-_5OUP1rSUkAQ&q=${pesquisaGoogle}`
  }
}

function fazerLogin() {
  window.location.href = "https://accounts.google.com/signin/v2/identifier?hl=pt-BR"
}


function sorte() {
  const pesquisaGoogle = pesquisa.value.replace(/\s/g, '+')

  window.location.href = `https://www.google.com/search?btnI=1&q=${pesquisaGoogle}`
}